import React from "react";
import { useGlobalContext } from "../../context";
import "./BookDetails.css";
import { useParams } from "react-router-dom";

const BookDetails = () => {
  const { books } = useGlobalContext();
  const { id } = useParams();

  const book = books.find((book) => book.id === id);

  if (!book) {
    return console.error();
  }

  return (
    <section>
      <div className="container-book-details font-radiocanada">
        <div className="flex ">
          <div className="left-column fixed flex flex-col mt-8 max-w-1/4">
            <img
              src={book.cover_id}
              alt="book cover"
              className="book-details mx-auto"
            />
            <div className="actions">
              <button className="justify-center mb-4 mt-4 rounded-3xl bg-darkwhite shadow-lg p-3 w-60">Want to read</button>
              <button className="justify-center mb-4 mt-4 rounded-3xl bg-darkwhite shadow-lg p-3 w-60">Want to read</button>
            </div>
          </div>
          <div className="right-column flex flex-col flex-1 overflow-y-auto ml-80 mt-8 mr-16">
            <h1 className="text-5xl">{book.title}</h1>
            <h2 className="mt-8 text-3xl text-grey">{book.author}</h2>
            <h3>{book.ratings_average} {book.ratings_count}</h3>
          </div>
        </div>
      </div>
    </section>
  );
};

export default BookDetails;
