import React, { useEffect } from "react";
import { useGlobalContext } from "../../context";
import "./BookDetails.css";
import { useParams } from "react-router-dom";

const URL = "https://openlibrary.org/works/"

const BookDetails = () => {
  const { books } = useGlobalContext();
  const { id } = useParams();
  const book = books.find((book) => book.id === id);

  useEffect(() => {
    const fetchBookDetails = async() => {
      try {
        const response = await fetch(`${URL}${id}.json`)
        const data = await response.json()
        console.log(data)

      } catch(error){
        console.log(error)
      }
    } 
    fetchBookDetails();

    const book = {

    }

  }, [id])

  if (!book) {
    return console.error();
  }

  return (
    <section>
      <div className="container-book-details font-radiocanada">
        <div className="flex ">
          <div className="left-column fixed flex flex-col mt-8 max-w-1/4">
            <img
              src={book.cover_id}
              alt="book cover"
              className="book-details mx-auto"
            />
            <button className="justify-center mb-8 mt-8 rounded-3xl bg-lightblue shadow-lg p-2 w-72">Want to read</button>
            <button className="justify-center rounded-3xl outline shadow-lg p-2 w-72">Kindle $13.99</button>
          </div>
          <div className="right-column flex flex-col flex-1 overflow-y-auto ml-96 mt-8 mr-8">
            <h1 className="text-5xl">{book.title}</h1>
            <h2 className="mt-4 text-3xl text-grey">{book.author}</h2>
            <p className="mt-4">{book.ratings_average} {book.ratings_count} ratings</p>
            <p>

            </p>
            <p>First published {book.first_publish_year}</p>
          </div>
        </div>
      </div>
    </section>
  );
};

export default BookDetails;
