import React, { useRef, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import { useGlobalContext } from "../../context";
import searchicon from '../../images/search.svg'

const Searchform = () => {
  const {setSearchTerm, setResultTitle} = useGlobalContext();
  const searchText = useRef('');
  const navigate = useNavigate();

  useEffect(() => searchText.current.focus(), []);
  const handleSubmit = (text) => {
    text.preventDefault();
    let tempSearchTerm = searchText.current.value.trim()
    if((tempSearchTerm.replace(/[^\w\s]/gi, "")).length === 0) {
      setSearchTerm("the lost world")
      setResultTitle("Please Enter Something")
    } else {
      setSearchTerm(searchText.current.value);
    }

    navigate('/search')
  }

  return (
    <div>
      <form className="bg-white border-b-beige rounded-xl p-4 shadow-lg opacity-1 flex" onSubmit={handleSubmit}>
          <input className="bg-transparent flex-1 outline-none custom-input w-full items-center placeholder-grey placeholder-opacity-25"
            type="Text"
            placeholder='Search by Book Title'
            ref = {searchText}
            style={{border : 'none'}}
          />
          <button className="bg-slategray hover:bg-steelblue border-b-beige rounded-xl p-2" type="text" onClick={handleSubmit}>
            <img className="w-4 h-4" src={searchicon} alt="search icon"/>
          </button>
      </form>
    </div>
  );
};

export default Searchform;
