import React, { useEffect } from 'react';
import './Home.css';
import lottie from 'lottie-web';
import Navbar from '../../Components/Navbar/Navbar';
import loading from '../../images/loading.gif';

const Home = () => {
  useEffect(() => {
    const animation = lottie.loadAnimation({
      container: document.getElementById('lottie-animation'),
      renderer: 'svg',
      loop: true,
      autoplay: true,
      path: '../../images/mainpageanimation.json'
    });
  }, []); 

  return (
    <main>
      <Navbar />
      <div className='container-home'>
        <div className='container-1 p-20 flex'>
          <div className='font-radiocanada flex flex-col'>
            <h1 className='font-extrabold text-center tracking-tighter text-6xl w-80'>
              The super fast book finder!
            </h1>
            <p className='text-center font-semibold text-xl max-w-80 text-grey my-8'>
              Find any book you're looking for or join our community of avid readers.
            </p>
            <button className='mx-auto rounded-xl bg-lightblue shadow-lg p-3 w-60'>
              Start Searching
            </button>
          </div>
          <div>
            <div id="lottie-animation" style={{ width: '400px', height: '400px' }}></div>
          </div>
        </div>
      </div>
    </main>
  );
}

export default Home;
