import React from 'react'
import { Link } from 'react-router-dom'
import './BookList.css'

const Book = (book) => {

  const trunc = (num) => {
    if (num != undefined) {
      return num.toFixed(2)
    } 
    else {
      return null
    }
  }

  return (
    <div className='my-5 p-2 bg-lightblue rounded-xl'>
      <div className='flex'>
        <img className="Book p-3" src={book.cover_img} alt = 'book cover' />
        <div className='p-5'>
          <h2 className='text-beige text-2xl hover:text-steelblue font-bold'>{book.title}</h2>
          <p className='text-beige text-2xl'> by {book.author}</p>
          <p> {trunc(book.ratings_average)} -- {book.ratings_count} rating</p>
        </div>
      </div>
    </div>
  )
}

export default Book

