import React from 'react'
import { Link } from 'react-router-dom'
import './BookList.css'

const Book = () => {

  const trunc = (num) => {
    if (num != undefined) {
      return num.toFixed(2)
    } 
    else {
      return null
    }
  }

  return (
    <div className='my-5 p-2 bg-darkwhite rounded-xl'>
      <div className='flex'>
        <Link to={`/search/book/${book.id}`} {...book}>
          <img className="Book p-1" src={book.cover_id} alt = 'book cover' />
        </Link>
        <div className='p-5'>
          <h2 className='text-beige text-2xl hover:text-lightblue font-bold mb-2'>{book.title}</h2>
          <p className='text-beige text-xl mb-1'> by {book.author}</p>
          <p className='text-beige text-m'> {trunc(book.ratings_average)} -- {book.ratings_count} ratings -- published {book.first_publish_year}</p>
        </div>
      </div>
    </div>
  )
}

export default Book

