import React from 'react'
import { useGlobalContext } from '../../context'
import Loading from '../Loader/Loader'
import './BookList.css'
import Searchform2 from '../SearchForm2/SearchForm2'
import Book from './Book'
import frame from '../../images/shittyframe.svg'
import { useContext } from 'react'
import { useState } from 'react'
import BookDetails from '../BookDetails/BookDetails'

const coverContext = React.createContext()

const Booklist = () => {
  const [cover, setCover] = useState("")
  const {books, loading} = useGlobalContext();
  const booksWithCovers = books.map((singleBook) => {
    return {
      ...singleBook,
      id: (singleBook.id).replace("/works/", ""),
      cover_img: singleBook.cover_id ? `https://covers.openlibrary.org/b/id/${singleBook.cover_id}-L.jpg` : frame
    }
  })

  setCover(singleBook.cover_id)

  if (loading) return <Loading />;

  return (
    <section>
      <div className='container'>
        <div className='flex flex-col'>
          <div className='mt-5 mb-2 bg-darkwhite rounded-xl p-10'>
            <Searchform2 />
          </div>
            <div className='section-title'>
              <div className=''>
                {booksWithCovers.slice(0, 30).map((item, index) => {
                  return (
                    <Book key={index} {...item} />
                  )
                })}
              </div>
            </div>
          </div>
      </div>
    </section>
  );
}

export default Booklist;
